import axios from "axios";

export default {
  state: {
    token: null,
    playersAdded: [],
  },
  mutations: {
    // мутації це по суті сетери, які додають дані в стейт
    // Payload (другий параметр) (state, payload) — це дані, які передаютсья нашою мутацією з компонента, який робить мутацію

    setToken(state, token) {
      console.log(token);
      state.token = token;
    },
    destroyToken(state) {
      state.token = null;
    },
    setPlayers(state, setPlayer) {
      state.playersAdded = setPlayer;
    },
    addNewPlayer(state, newPlayer) {
      console.log(newPlayer);
      state.playersAdded.push(newPlayer);
    },
  },
  actions: {
    // Мутації є синхронними і ми не можемо запускати асинхронні операції, такі як виклики API, всередині мутації
    // Що робити в такій ситуації? Створювати Дії  (Actions).
    // Дії подібні на мутації, але замість того, щоб напряму змінювати стан (стейт), вони роблять мутацію

    authUser({ commit }, authData) {
      const key = "AIzaSyAOyTrULzGd7rbuhaDsFWIMR4V6hmdWV3A";
      return axios
        .post(
          `https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${key}`,
          {
            email: authData.email,
            password: authData.password,
            returnSecureToken: true,
          }
        )
        .then((res) => {
          let token = res.data.idToken;
          commit("setToken", token);
          localStorage.setItem("token", token);
        })
        .catch((e) => {
          console.log("Zalupa Horse");
          this.$router.push("/auth");
        });
    },
    // по факту цей екшн не використовується, а просто в routes.js прописана та ж логіка
    // є одне "але" - через routes.js не відправляється коміт "setToken" і при перезавантажені
    // сторінки в стейті пропадає токен, але зберігається в localStorage. Все ж таки - не викидає.
    // ШЛЯПА - треба пофіксити!
    initAuth({ commit }) {
      let token = localStorage.getItem("token");
      if (!token) {
        return false;
      } else {
        commit("setToken", token);
      }
    },
    logoutUser({ commit }) {
      // commit це відправка мутації !!!!!
      commit("destroyToken");
      localStorage.removeItem("token");
    },
    loadPlayers({ commit }) {
      return axios
        .get(
          "https://okoloukola-13e0b-default-rtdb.firebaseio.com/players.json"
        )
        .then((res) => {
          console.log(res);
          commit("setPlayers", res);
        });
    },
    addNewPlayer({ commit }, newPlayer) {
      return axios
        .post(
          "https://okoloukola-13e0b-default-rtdb.firebaseio.com/players.json",
          newPlayer
        )
        .then((res) => {
          commit("addNewPlayer", newPlayer);
          /* console.log(res); */
        })
        .catch((e) => console.log(e));
    },
  },

  getters: {
    checkAuthUser(state) {
      return state.token != null;
    },
    auth(state) {
      return state.user;
    },
  },
};
