import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    message: "Hello Vuex",
  },
  mutations: {
    // не працює з асинхронними даними!!
    setMessage(state) {
      state.message = "Hello again";
    },
  },
  actions: {
    // працює з асинхронними даними
    setMessage({ commit }) {
      commit("setMessage");
    },
  },
  getters: {
    getMessage(state) {
      return state.message;
    },
  },
});
