/* import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  modules: {},
}); */

export default {
  state: {
    message: "Hello Vuex",
  },
  mutations: {
    // не працює з асинхронними даними!!
    setMessage(state, message) {
      state.message = message;
    },
  },
  actions: {
    // працює з асинхронними даними
    setMessage({ commit }, payload) {
      commit("setMessage", payload);
    },
  },
  getters: {
    getMessage(state) {
      return state.message;
    },
  },
};
